import { setStatistics } from './statisticsSlice';
import { fetchStatistics } from '../services/api';

export const fetchStatisticsAsync = () => async (dispatch) => {
  try {
    const response = await fetchStatistics();

    if (response.ok) {
      // Check if response.data is defined before accessing it
      if (response.data) {
        dispatch(setStatistics(response.data));
      } else {
        console.error('Invalid response format: response.data is undefined', response);
      }
    } else {
      console.error('Failed to fetch statistics. Server returned an error:', response.statusText);
    }
  } catch (error) {
    console.error('Error during fetchStatisticsAsync:', error);
  }
};
