import { configureStore } from '@reduxjs/toolkit';
import { combineReducers } from 'redux';
import createSagaMiddleware from 'redux-saga';
import songsReducer from './songsSlice';
import statisticsReducer from './statisticsSlice';
import rootSaga from '../sagas';
import { fetchStatisticsAsync } from './statisticsActions';

// Define root reducer
const rootReducer = combineReducers({
  songs: songsReducer,
  statistics: statisticsReducer,
});

// Create saga middleware
const sagaMiddleware = createSagaMiddleware();

// Create Redux store
const store = configureStore({
  reducer: rootReducer,
  middleware: (getDefaultMiddleware) => getDefaultMiddleware().concat(sagaMiddleware),
});

// Run root saga
sagaMiddleware.run(rootSaga);

// Dispatch fetch statistics action (dispatch it where needed in your application, not during store creation)
// For example, in a component or in the useEffect of a component
// store.dispatch(fetchStatisticsAsync());

// Export store
export default store;
