import React, { useState, ChangeEvent } from 'react';
import { useDispatch } from 'react-redux';
import { addSongFetch, SongType } from '../songState/songsState';
import styled from '@emotion/styled';

// Styled components
const Container = styled.div`
  margin: 20px;
`;

const ToggleButton = styled.button`
  padding: 10px;
  margin-bottom: 10px;
  background-color: #4caf50;
  color: white;
  border: none;
  cursor: pointer;
`;

const Form = styled.form`
  display: none;
  margin-top: 10px;

  ${({ show }) => show && `
    display: block;
  `}
`;

const FormLabel = styled.label`
  display: block;
  margin-bottom: 10px;
`;

const FormInput = styled.input`
  width: 100%;
  padding: 8px;
  margin-top: 5px;
  margin-bottom: 10px;
  box-sizing: border-box;
`;

const SubmitButton = styled.button`
  background-color: #4caf50;
  color: white;
  padding: 10px;
  border: none;
  cursor: pointer;
`;

const AddSong: React.FC = () => {
  const dispatch = useDispatch();

  const [newSong, setNewSong] = useState<SongType>({
    title: '',
    artist: '',
    album: '',
    genre: '',
  });

  // State to track whether the form should be displayed
  const [showForm, setShowForm] = useState(false);

  const handleAddSong = () => {
    dispatch({
      type: 'songs/addSongFetch',
      payload: {
        title: newSong.title,
        artist: newSong.artist,
        album: newSong.album,
        genre: newSong.genre,
      },
    });

    setNewSong({
      title: '',
      artist: '',
      album: '',
      genre: '',
    });

    // After adding the song, hide the form
    setShowForm(false);
  };

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setNewSong((prevSong) => ({
      ...prevSong,
      [name]: value,
    }));
  };

  return (
    <Container>
      {/* Display button to toggle the form visibility */}
      <ToggleButton type="button" onClick={() => setShowForm(!showForm)}>
        {showForm ? 'Hide Form' : 'Add Song'}
      </ToggleButton>

      {/* Display the form only when showForm is true */}
      <Form show={showForm}>
        <FormLabel>
          Title:
          <FormInput type="text" name="title" value={newSong.title} onChange={handleChange} />
        </FormLabel>
        <FormLabel>
          Artist:
          <FormInput type="text" name="artist" value={newSong.artist} onChange={handleChange} />
        </FormLabel>
        <FormLabel>
          Album:
          <FormInput type="text" name="album" value={newSong.album} onChange={handleChange} />
        </FormLabel>
        <FormLabel>
          Genre:
          <FormInput type="text" name="genre" value={newSong.genre} onChange={handleChange} />
        </FormLabel>
        <SubmitButton type="button" onClick={handleAddSong}>
          Add Song
        </SubmitButton>
      </Form>
    </Container>
  );
};

export default AddSong;
