import React, { useEffect, useState } from 'react';
import { getSongsFetch, rootState } from '../songState/songsState';
import { useDispatch, useSelector } from 'react-redux';
import styled from '@emotion/styled';

const StyledList = styled.ul`
  list-style-type: none;
  cursor: pointer;
`;

const GetSong: React.FC = () => {
  const dispatch = useDispatch();
  const songs = useSelector((state: rootState) => state.songs.songs);
  const allArtists = Array.from(new Set(songs.map((item) => item.artist)));
  const [displayedArtists, setDisplayedArtists] = useState<string[]>(allArtists);

  useEffect(() => {
    dispatch(getSongsFetch());
  }, [dispatch]);

  const handleArtistClick = (artist: string) => {
    setDisplayedArtists([artist]);
  };

  return (
    <div>
      <h1>Artists</h1>
      <StyledList>
        {allArtists.map((artist, index) => (
          <li key={index} onClick={() => handleArtistClick(artist)}>
            {artist}
          </li>
        ))}
      </StyledList>
    </div>
  );
};

export default GetSong;
